import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import Form from './components/Form/Form'
import Main from './components/Main/Main'


class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
        surname: '',
        name: '',
        patronymic: '',
        identif: '',
        doc_series: '',
        doc_num: '',
        items: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    const valueUpperCase = value.toUppercase();

    this.setState({[name]: valueUpperCase});
  }

  handleSubmit(event) {
      let url = `https://a.todes.by:13555/data-service-test/api/v1/data?sys_organ=26&identif=${this.state.identif}&surname%20=${this.surname}&name=${this.state.name}&patronymic=${this.state.patronymic}&doc_series=${this.state.doc_series}&doc_num=${this.state.doc_num}`

      fetch(url)
      .then(res => res.json())
      .then(
          (result) => {
              this.setState({
                  items: result
              });
          },
          (error) => {
              this.setState({
                  error
              });
          }
      )
      event.preventDefault();   
  }

  render () {

    return (
      <div className="App">
        <Form 
          surname={this.state.surname}
          name={this.state.name}
          patronymic={this.state.patronymic}
          identif={this.state.identif}
          doc_series={this.state.doc_series}
          doc_num={this.state.doc_num}
          items={this.state.items}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
        <Main items={this.state.items}/>
      </div>
    );
  }
}

export default App;
