import React from 'react';
import TablePagination from '@material-ui/core/TablePagination'
import Result from './Result'


function TablePaginationDemo() {
    const [page, setPage] = React.useState(2);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    const handleChangePage = (event, newPage) => {
      setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
      setRowsPerPage(parseInt(event.target.value, 10));
      setPage(0);
    };
    return (
        <TablePagination
          component="div"
          count={100}
          page={page}
          onChangePage={handleChangePage}
          rowsPerPage={rowsPerPage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      );
    }


class Main extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            currentPage: 1,
            todosPerPage: 5
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(event) {
        this.setState({
          currentPage: Number(event.target.id)
        });
    }

    render () {

        const { currentPage, todosPerPage } = this.state;


        const indexOfLastTodo = currentPage * todosPerPage;
        const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
        const currentTodos = this.props.items.slice(indexOfFirstTodo, indexOfLastTodo);

        // display Todos
        const renderTodos = currentTodos.map((item, index) => {
            return (
                <div key={index}>
                    <Result 
                        surname={item.surname} 
                        name={item.name}
                        sname={item.sname}
                        bdate={item.bdate}
                        identif={item.identif}
                        addressLast={item.addressLast.typeCityL + ' ' + item.addressLast.cityL + ' ' + item.addressLast.typeStreetL + ' ' + item.addressLast.streetL + ' ' + item.addressLast.house + ', ' + item.addressLast.app}
                        ateAddress={item.ateAddress}
                        dsdDateRec={item.dsdDateRec}
                    />
                </div>
            )
        });

        // display pageNumber  
        const pageNumbers = [];
        for (let i = 1; i <= Math.ceil(this.props.items.length / todosPerPage); i++) {
            pageNumbers.push(i);
        }

        // const indexOfLastNum = currentPage * todosPerPage;
        // const indexOfFirstNum = indexOfLastNum - todosPerPage;
        // const currentNum = pageNumbers.slice(indexOfFirstNum, indexOfLastNum);

        // const maxPageNumber = Math.max(...pageNumbers);
        // console.log(maxPageNumber);

        const renderPageNumbers = pageNumbers.map(number => {
            return (
              <li className="page-item page-link active"
                key={number}
                id={number}
                onClick={this.handleClick}
              >
                {number}
              </li>
            );
        });


        return (
            <div className='main'>
                <nav className='shadow-lg pb-3 mb-3 bg-body rounded main-nav'>
                <a className='main-nav-a' href='/'>Главная</a>
                </nav>

                <div className='main-container'>
                    <div className="input-group mb-3">
                        <span className="input-group-text" id="basic-addon1">Поиск</span>
                        <input type="text" className="form-control"  aria-label="Username" aria-describedby="basic-addon1" />
                    </div>

                    <div className='table'>
                        <div className='table-head'>
                            <span>Результаты поиска</span>
                        </div>

                        <div className='div-row'>
                            <div className='div-col'>Фамилия</div>
                            <div className='div-col'>Имя</div>
                            <div className='div-col'>Отчество</div>
                            <div className='div-col'>Дата рождения</div>
                            <div className='div-col'>Идентификационный номер</div>
                            <div className='div-col'>Адрес регистрации (после конвертации)</div>
                            <div className='div-col'>Адрес регистрации (коды АТЕ и ТЕ)</div>
                            <div className='div-col'>Дата регистрации</div>
                        </div>
                        {renderTodos}

                        <div className='table-head'>
                            <nav aria-label="Page navigation example">
                                <ul className="pagination justify-content-center">
                                    <li className="page-item page-link active" aria-label="Previous" onClick={this.handleClick}>
                                        <span aria-hidden="true">&laquo;</span>
                                    </li>
                                    {renderPageNumbers}
                                    <li className="page-item page-link active" aria-label="Next" onClick={this.handleClick}>
                                        <span aria-hidden="true">&raquo;</span>
                                    </li>
                                </ul>
                            </nav>
                        </div>
                        <TablePagination
                            component="div"
                            count={100}
                            page={page}
                            onChangePage={handleChangePage}
                            rowsPerPage={rowsPerPage}
                            onChangeRowsPerPage={handleChangeRowsPerPage}
                        />
                    </div>

                </div>
            </div>
        )
    }
}

export default (Main);
