import React from 'react';
import './Form'




class Form extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            surname: '',
            name: '',
            patronymic: '',
            identif: '',
            doc_series: '',
            doc_num: '',
            items: { },
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const target = event.target;
        const name = target.name;
        const value = target.value;

        this.setState({[name]: value});
    }

    handleSubmit(event) {
        let url = `https://a.todes.by:13555/data-service-test/api/v1/data?sys_organ=26&identif=${this.state.identif}&surname%20=${this.surname}&name=${this.state.name}&patronymic=${this.state.patronymic}&doc_series=${this.state.doc_series}&doc_num=${this.state.doc_num}`

        fetch(url)
        .then(res => res.json())
        .then(
            (result) => {
                let data = result;
                this.setState({
                    items: data
                })
                console.log(data);
            }
        )
        event.preventDefault();
        console.log([
            this.state.surname, 
            this.state.name, 
            this.state.patronymic,
            this.state.identif,
            this.state.doc_series,
            this.state.doc_num,
            this.state.items
        ]);
    }

    render () {
        return (
            <div className='shadow-lg pb-3 mb-5 bg-body rounded reg-acc'>
                <div className='p-3 mb-2 bg-primary text-white position'>
                    <h4>Регистрационный учет</h4>
                    <div className='circle'></div>
                </div>

                <form className='form' onSubmit={this.handleSubmit}>
                    <div>
                        <label className="form-label">
                        Фамилия
                        <input className='form-control' type='text' name='surname' placeholder='ВВЕДИТЕ ФАМИЛИЮ' value={this.state.surname} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className="form-label">
                        Имя
                        <input className='form-control' type='text' name='name' placeholder='ВВЕДИТЕ ИМЯ' value={this.state.name} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className="form-label">
                        Отчество
                        <input className='form-control' type='text' name='patronymic' placeholder='ВВЕДИТЕ ОТЧЕСТВО' value={this.state.patronymic} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className="form-label">
                        Идентификационный нормер
                        <input className='form-control' type='text' name='identif' placeholder='ВВЕДИТЕ НОМЕР' value={this.state.identif} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className='form-label doc_series'>
                        Серия
                        <input className='form-control' type='text' name='doc_series' value={this.state.doc_series} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className='form-label doc_num'>
                        № паспорта
                        <input className='form-control' type='number' name='doc_num' value={this.state.doc_num} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <input className="btn btn-primary btn-width" type='submit' value='Поиск в локальной БД'  />
                    </div>
                </form>
                <div><span>{this.state.surname}</span></div>
            </div>
        )
    }
}

export default Form;
