import React from 'react';
import './Form'




class Form extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            surname: '',
            name: '',
            patronymic: '',

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const target = event.target;
        const surname = target.surname;
        const name = target.name;
        const patronymic = target.patronymic;

        this.setState({[name]: value});
        // this.setState({
        //     surname: event.target.surname,
        //     name: event.target.name,
        //     patronymic: event.target.patronymic,
        // });
    }

    handleSubmit(event) {
        alert('Отправленное имя: ' + this.state.surname + this.state.name + this.state.patronymic);
        event.preventDefault();
    }

    render () {
        return (
            <div className='shadow-lg pb-3 mb-5 bg-body rounded reg-acc'>
                <div className='p-3 mb-2 bg-primary text-white position'>
                    <h4>Регистрационный учет</h4>
                    <div className='circle'></div>
                </div>

                <form className='form' onSubmit={this.handleSubmit}>
                    <div>
                        <label className="form-label">
                        Фамилия
                        <input className='form-control' type='text' name='surname' placeholder='ВВЕДИТЕ ФАМИЛИЮ' value={this.state.surname} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className="form-label">
                        Имя
                        <input className='form-control' type='text' name='name' placeholder='ВВЕДИТЕ ИМЯ' value={this.state.name} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className="form-label">
                        Отчество
                        <input className='form-control' type='text' name='patronymic' placeholder='ВВЕДИТЕ ОТЧЕСТВО' value={this.state.patronymic} onChange={this.handleChange} />
                        </label>
                    </div>

                    <div>
                        <label className="form-label">
                        Идентификационный нормер
                        <input className='form-control' type='number' name='identif' placeholder='ВВЕДИТЕ НОМЕР' />
                        </label>
                    </div>

                    <div>
                        <label className='form-label doc_series'>
                        Серия
                        <input className='form-control' type='text' name='doc_series' />
                        </label>
                    </div>

                    <div>
                        <label className='form-label doc_num'>
                        № паспорта
                        <input className='form-control' type='number' name='doc_num' />
                        </label>
                    </div>

                    <div>
                        <input className="btn btn-primary btn-width" type='submit' value='Поиск в локальной БД'  />
                    </div>
                </form>
            </div>
        )
    }
}

export default Form;
